{
  "type": "Catalog",
  "id": "item-catalog-for-my-data",
  "stac_version": "1.1.0",
  "description": "A useful description about the datasets in my project.",
  "links": [
    {
      "rel": "root",
      "href": "./catalog.json",
      "type": "application/json",
      "title": "Item Catalog Example"
    },
    {
      "rel": "item",
      "href": "./item_1/item_1.json",
      "type": "application/geo+json"
    },
  ],
  "title": "Item Catalog Example"
}
